import {Button, Col, Container, Row} from 'react-bootstrap';
import DatesCount from './components/DatesCount';
import { person } from './data';
function App() {
/*   sm small 12 col 
py padding from top and bottom y-axis
px padding on x-axis
mx-3 margin x-axis
d-inline as one line
fs-5 fontsize
border-bottom ready library at react bootstrap as <br>
 */

  return (
    <div className='font color-body'>

      <Container >
      <DatesCount personp={person}/>

      <Row className='justify-content-center'>
      <Col sm='8' className=''>

      <div className='rectangle'>
      {person.length ? (person.map((item)=>{
        return(<div className="d-flex border-bottom mx-3 my-3">
        <img className='img-style'  src='matched-bg2-designify.png'  alt='not found img'/>
  <div className='px-2 py-3 '>
  <p className=' fs-7 my-0'> {item.name}</p>  
  <p className=' fs-8 my-0'>{item.date}</p>
  </div>
        </div>)
      })):<h2 className="p-2 text-center">لا يوجد مواعيد اليوم</h2>}  {/* or null */}
      </div>
      </Col>
       </Row>


       <Row className='justify-content-center my-2'>
     <Col sm='8' className='d-flex justify-content-between'>
     <Button className="btn btn-primary p-2">مسح الكل</Button>
     <Button className="btn btn-primary p-2">عرض الكل</Button> 

     </Col>
      </Row>
      </Container>
    </div>
  );
}

export default App;
