import DatesCount from './components/DatesCount';
import DatesList from './components/DatesList';
import { person } from './data';
function App() {
/*   sm small 12 col 
py padding from top and bottom y-axis
px padding on x-axis
mx-3 margin x-axis
d-inline as one line
fs-5 fontsize
border-bottom ready library at react bootstrap as <br>
 */

  return (
    <div className='font color-body'>

      <Container >
      <DatesCount personp={person}/>
      <DatesList/>



       <Row className='justify-content-center my-2'>
     <Col sm='8' className='d-flex justify-content-between'>
     <Button className="btn btn-primary p-2">مسح الكل</Button>
     <Button className="btn btn-primary p-2">عرض الكل</Button> 

     </Col>
      </Row>
      </Container>
    </div>
  );
}

export default App;
